import 'package:flutter/material.dart';
import 'dart:convert';
import 'package:cached_network_image/cached_network_image.dart';

void main() => runApp(BeritaApp());

class Berita {
  final String title;
  final String description;
  final String imageUrl;
  Berita({
    required this.title,
    required this.description,
    required this.imageUrl,
  });
}

class BeritaApp extends StatelessWidget {
  Widget beritaImage(String url) {
    if (url.startsWith('data:image')) {
      final base64Str = url.split(',').last;
      return Image.memory(
        base64Decode(base64Str),
        width: 60,
        height: 60,
        fit: BoxFit.cover,
      );
    } else if (url.startsWith('http')) {
      return CachedNetworkImage(
        imageUrl: url,
        width: 60,
        height: 60,
        fit: BoxFit.cover,
        errorWidget: (context, url, error) =>
            Icon(Icons.broken_image, size: 60),
        placeholder: (context, url) => Container(
          width: 60,
          height: 60,
          alignment: Alignment.center,
          child: CircularProgressIndicator(strokeWidth: 2),
        ),
      );
    } else {
      return Image.asset(
        url,
        width: 60,
        height: 60,
        fit: BoxFit.cover,
        errorBuilder: (context, error, stackTrace) =>
            Icon(Icons.broken_image, size: 60),
      );
    }
  }

  final List<Berita> daftarBerita = [
    Berita(
      title: 'Flutter 3.0 Resmi Dirilis',
      description:
          'Framework Flutter versi terbaru hadir dengan banyak fitur baru.',
      imageUrl:
          'data:image/jpeg;base64,',
    ),
    Berita(
      title: 'Prospek Kerja Program Studi Sistem Informasi',
      description: 'Sistem Informasi menawarkan berbagai peluang karir.',
      imageUrl:
          'data:image/jpeg;base64,',
    ),
    Berita(
      title: 'Mobile Programming Populer',
      description: 'Pengembangan aplikasi mobile semakin diminati.',
      imageUrl:
          'https://th.bing.com/th/id/OIP.oM8a3uys4znaNpDu654ztwHaEs?w=274&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7',
    ),
  ];

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Daftar Berita',
      home: Scaffold(
        appBar: AppBar(title: Text('Daftar Berita')),
        body: ListView.builder(
          itemCount: daftarBerita.length,
          itemBuilder: (context, index) {
            final berita = daftarBerita[index];
            return ListTile(
              leading: beritaImage(berita.imageUrl),
              title: Text(berita.title),
              subtitle: Text(berita.description),
              trailing: Icon(Icons.bookmark_border),
              onTap: () {
                ScaffoldMessenger.of(context).showSnackBar(
                  SnackBar(content: Text('Mengalihkan ke halaman berita')),
                );
              },
            );
          },
        ),
      ),
    );
  }
}


